import './App.css'

import { ethers } from 'ethers'
import React, { useCallback, useEffect, useState } from 'react'

// 导入 ABI
import MarketABI from './abis/Market.json'
import MyNFTABI from './abis/MyNFT.json'

// 这里是你的合约地址
const MYNFT_ADDRESS = "0x414D716fbd0F8B708dB7342B1Ac681939B49779F";
const MARKET_ADDRESS = "0x8B23398cbd6BC673e67cd76faE7EA21c967Ba861";

function App() {
  const [account, setAccount] = useState(null);
  // const [provider, setProvider] = useState(null);
  const [nftContract, setNftContract] = useState(null);
  const [marketContract, setMarketContract] = useState(null);
  const [tokenId, setTokenId] = useState('');
  const [tokenCID, setTokenCID] = useState('');
  const [price, setPrice] = useState('');
  const [nftsForSale, setNftsForSale] = useState([]);
  const [userNFTs, setUserNFTs] = useState([]);
  const [loading, setLoading] = useState(false);

  // 连接到 MetaMask
  const connectWallet = async () => {
    if (window.ethereum) {
      const newProvider = new ethers.providers.Web3Provider(window.ethereum);
      const newSigner = newProvider.getSigner();
      const newAccount = await newSigner.getAddress();
      setAccount(newAccount);
      // setProvider(newProvider);

      const nftContractInstance = new ethers.Contract(MYNFT_ADDRESS, MyNFTABI.abi, newSigner);
      setNftContract(nftContractInstance);

      const marketContractInstance = new ethers.Contract(MARKET_ADDRESS, MarketABI.abi, newSigner);
      setMarketContract(marketContractInstance);
    } else {
      alert('Please install MetaMask!');
    }
  };

  // 铸造 NFT
  const mintNFT = async () => {
    if (!nftContract || !tokenCID) return;

    setLoading(true);
    try {
      const transaction = await nftContract.safeMint(account, tokenCID);
      await transaction.wait();
      alert('NFT Minted Successfully!');
    } catch (err) {
      console.error(err);
      alert('Error minting NFT');
    } finally {
      setLoading(false);
    }
  };

  // 列出 NFT 出售
  const listNFTForSale = async () => {
    if (!marketContract || !tokenId || !price) return;

    setLoading(true);
    try {
      const transaction = await marketContract.listNFTForSale(tokenId, ethers.utils.parseEther(price));
      await transaction.wait();
      alert('NFT listed for sale!');
    } catch (err) {
      console.error(err);
      alert('Error listing NFT for sale');
    } finally {
      setLoading(false);
    }
  };

  // 取消出售
  const delistNFT = async (id) => {
    if (!marketContract) return;

    setLoading(true);
    try {
      const transaction = await marketContract.delistNFT(id);
      await transaction.wait();
      alert('NFT delisted from sale');
    } catch (err) {
      console.error(err);
      alert('Error delisting NFT');
    } finally {
      setLoading(false);
    }
  };

  // 购买 NFT
  // const buyNFT = async (tokenId, price) => {
  //   if (!account || !marketContract || !nftContract) return;

  //   try {
  //     setLoading(true);

  //     const priceInWei = ethers.utils.parseEther(price); // 将 ETH 转为 Wei（以太坊最小单位）

  //     // 调用合约购买 NFT
  //     const tx = await marketContract.buyNFT(tokenId, { value: priceInWei });

  //     // 等待交易完成
  //     await tx.wait();

  //     alert('NFT purchased successfully!');
  //     fetchNftsForSale(); // 重新加载市场数据
  //   } catch (err) {
  //     console.error(err);
  //     alert('Error buying NFT');
  //   } finally {
  //     setLoading(false);
  //   }
  // };  

  // 获取nft交易列表
  const fetchNftsForSale = useCallback(async () => {
    if (!marketContract || !nftContract) return;

    try {
      const totalSupply = await nftContract.totalSupply();
      console.log('totalSupply:', totalSupply.toString()); // 输出 totalSupply 以确认

      const nfts = [];
      for (let i = 0; i < totalSupply.toNumber(); i++) { // 使用 toNumber() 来确保是数字
        const tokenId = await nftContract.tokenByIndex(i);
        console.log('tokenId:', tokenId.toString()); // 输出 tokenId

        const priceBigNumber = await marketContract.getPrice(tokenId);
        console.log('priceBigNumber:', priceBigNumber.toString()); // 输出 priceBigNumber

        const price = ethers.utils.formatEther(priceBigNumber).toString(); // 将 BigNumber 转换为 ETH 字符串
        console.log('price formatted as ETH:', price);

        const isForSale = await marketContract.isForSale(tokenId);
        console.log('isForSale:', isForSale);

        if (isForSale) {
          // 确保这里的 tokenId 和 price 是字符串类型
          nfts.push({
            tokenId: tokenId.toString(),
            price: price, // 将价格确保是字符串
          });
        }
      }

      setNftsForSale(nfts);
    } catch (err) {
      console.error(err);
      alert('Error fetching NFTs for sale');
    }
  }, [marketContract, nftContract]);



  // 获取用户的所有 NFT
  const fetchUserNFTs = useCallback(async () => {
    if (!nftContract || !account) return;

    try {
      const totalSupply = await nftContract.totalSupply();
      const userNFTs = [];
      for (let i = 0; i < totalSupply; i++) {
        const tokenId = await nftContract.tokenByIndex(i);
        const owner = await nftContract.ownerOf(tokenId);
        if (owner === account) {
          userNFTs.push(tokenId);
        }
      }
      setUserNFTs(userNFTs);
    } catch (err) {
      console.error(err);
      alert('Error fetching user NFTs');
    }
  }, [nftContract, account]);

  // 页面加载时获取市场上的 NFT
  useEffect(() => {
    if (marketContract) {
      fetchNftsForSale();
    }
  }, [marketContract, nftContract, fetchNftsForSale]);

  // 页面加载时获取用户的 NFT
  useEffect(() => {
    if (nftContract && account) {
      fetchUserNFTs();
    }
  }, [nftContract, account, fetchUserNFTs]);  

  return (
    <div className="App">
      <h1>NFT Marketplace</h1>
      {!account ? (
        <button onClick={connectWallet}>Connect Wallet</button>
      ) : (
        <div>
          <p>Connected as {account}</p>
          <h2>Mint an NFT</h2>
          <input
            type="text"
            placeholder="Enter Token CID"
            value={tokenCID}
            onChange={(e) => setTokenCID(e.target.value)}
          />
          <button onClick={mintNFT} disabled={loading}>
            {loading ? 'Minting...' : 'Mint NFT'}
          </button>

          <h2>List NFT for Sale</h2>
          <input
            type="number"
            placeholder="Enter Token ID"
            value={tokenId}
            onChange={(e) => setTokenId(e.target.value)}
          />
          <input
            type="text"
            placeholder="Enter Price (ETH)"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
          <button onClick={listNFTForSale} disabled={loading}>
            {loading ? 'Listing...' : 'List NFT'}
          </button>

          <h2>Your NFTs</h2>
          {userNFTs.length === 0 ? (
            <p>You don't own any NFTs yet.</p>
          ) : (
            <ul>
              {userNFTs.map((id) => (
                <li key={id}>
                  Token ID: {id}
                  <button onClick={() => delistNFT(id)} disabled={loading}>
                    {loading ? 'Removing...' : 'Remove from Sale'}
                  </button>
                </li>
              ))}
            </ul>
          )}

          <h2>NFTs for Sale</h2>

          {nftsForSale.length === 0 ? (
            <p>No NFTs for sale.</p>
          ) : (
            <ul>
              {nftsForSale.map(({ tokenId, price }) => {
                console.log('Raw price:', price);
                 // 确保 price 是 BigNumber 类型并转换为 ETH 格式
                const priceInEth = price ? ethers.utils.formatUnits(price, 18) : "0.0"; // 18 位是标准的 ETH 小数位数

                console.log('Rendering price:', priceInEth); // 输出转换后的价格
                 return(
                  <li key={tokenId}>
                    Token ID: {tokenId}, Price: {price} ETH
                    {/* <button onClick={() => buyNFT(tokenId, price)} disabled={loading}> */}
                      {/* {loading ? 'Purchasing...' : 'Buy NFT'}
                    </button> */}
                  </li>
                 );
              })}
            </ul>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
