import { ethers } from 'ethers'
import React, { useEffect, useState } from 'react'
import './App.css'

// 导入 ABI
import MarketABI from './abis/Market.json'
import MyNFTABI from './abis/MyNFT.json'

// 这里是你的合约地址
const MYNFT_ADDRESS = "0x4C4b321C3eFd7875073412E4c514226dd828A389";
const MARKET_ADDRESS = "0x4C4f3573008CB476721038535bB7906Cb4AfC00A";

function App() {
  const [account, setAccount] = useState(null);
  const [provider, setProvider] = useState(null);
  const [nftContract, setNftContract] = useState(null);
  const [marketContract, setMarketContract] = useState(null);
  const [tokenId, setTokenId] = useState('');
  const [tokenCID, setTokenCID] = useState('');
  const [price, setPrice] = useState('');
  const [nftsForSale, setNftsForSale] = useState([]);
  const [loading, setLoading] = useState(false);

  // 连接到 MetaMask
  const connectWallet = async () => {
    if (window.ethereum) {
      const newProvider = new ethers.providers.Web3Provider(window.ethereum);
      const newSigner = newProvider.getSigner();
      const newAccount = await newSigner.getAddress();
      setAccount(newAccount);
      setProvider(newProvider);

      const nftContractInstance = new ethers.Contract(MYNFT_ADDRESS, MyNFTABI.abi, newSigner);
      setNftContract(nftContractInstance);

      const marketContractInstance = new ethers.Contract(MARKET_ADDRESS, MarketABI.abi, newSigner);
      setMarketContract(marketContractInstance);
    } else {
      alert('Please install MetaMask!');
    }
  };

  // 铸造 NFT
  const mintNFT = async () => {
    if (!nftContract || !account) return;
    try {
      setLoading(true);
      const tx = await nftContract.safeMint(account, tokenCID);
      await tx.wait();
      alert('NFT Minted!');
    } catch (error) {
      console.error('Error minting NFT', error);
    } finally {
      setLoading(false);
    }
  };

  // 上架 NFT
  const listNFT = async () => {
    if (!marketContract || !tokenId || !price || !account) return;
    try {
      setLoading(true);
      const priceInWei = ethers.utils.parseUnits(price, 'ether'); // 将价格转换为 Wei
      const tx = await marketContract.listNFT(MYNFT_ADDRESS, tokenId, priceInWei);
      await tx.wait();
      alert('NFT Listed for Sale!');
    } catch (error) {
      console.error('Error listing NFT', error);
    } finally {
      setLoading(false);
    }
  };

  // 获取待售 NFT
  const fetchNFTsForSale = async () => {
    if (!marketContract) return;
    try {
      const nfts = await marketContract.getAllNFTsForSale();
      setNftsForSale(nfts);
    } catch (error) {
      console.error('Error fetching NFTs for sale', error);
    }
  };

  useEffect(() => {
    if (account) {
      fetchNFTsForSale(); // 获取待售NFT列表
    }
  }, [account]);

  return (
    <div className="App">
      <header className="App-header">
        <h1>NFT Marketplace</h1>

        {!account ? (
          <button onClick={connectWallet}>Connect Wallet</button>
        ) : (
          <div>
            <p>Connected Account: {account}</p>
          </div>
        )}

        <div>
          <h2>Mint NFT</h2>
          <input
            type="text"
            placeholder="Enter token CID"
            value={tokenCID}
            onChange={(e) => setTokenCID(e.target.value)}
          />
          <button onClick={mintNFT} disabled={loading}>
            {loading ? 'Minting...' : 'Mint NFT'}
          </button>
        </div>

        <div>
          <h2>List NFT for Sale</h2>
          <input
            type="number"
            placeholder="Enter token ID"
            value={tokenId}
            onChange={(e) => setTokenId(e.target.value)}
          />
          <input
            type="text"
            placeholder="Enter price in ETH"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
          <button onClick={listNFT} disabled={loading}>
            {loading ? 'Listing...' : 'List NFT for Sale'}
          </button>
        </div>

        <div>
          <h2>NFTs for Sale</h2>
          {nftsForSale.length > 0 ? (
            <ul>
              {nftsForSale.map((nft, index) => (
                <li key={index}>
                  Token ID: {nft.tokenId}, Price: {ethers.utils.formatUnits(nft.price, 'ether')} ETH
                </li>
              ))}
            </ul>
          ) : (
            <p>No NFTs available for sale.</p>
          )}
        </div>
      </header>
    </div>
  );
}

export default App;
